import React, { useState } from 'react';
import './App.css';

function App() {
  const [counter, setCounter] = useState(0);

  const addCounter = () => {
    setCounter(count + 1)
  }

  return (
    <div className="App">
      <div className='header-container'>
        <button>-</button>
        <button>Clear</button>
        <button onClick={addCounter}>+</button>
      </div> 
      <div className='counter-container'>
        <span>Counter: {counter}</span>
      </div>
    </div>
  );
}

export default App;
